def journalistes
	#On initialise les variables.
	num = 0; aude = 0; maj = 0; maj2 = 0; unders = 0
	#On créé une boucle pour parcourir l'array "twitter"
	@twitter.each do |user|
		#On compte le nombre de twitters incluant un chiffre. Si  supérieur à 0, on incrémente un compteur.
		if user.count("0-9") > 0
			num += 1
		end
		#Ici on recherche les sous-string "aude" dans les différentes string. Après les avoir downcase préalablement.
		if user.downcase.include?("aude")
			aude += 1
		end
		#Si la première lettre apres le "@" est une majuscule, incrémente le compteur "maj"
		if user[1].count("A-Z") > 0
			maj += 1
		end
		#Même méthode que pour les chiffres 
		if user.count("A-Z") > 0
			maj2 += 1
		end
		#Ici j'ajoute le nombre d'underscore présent dans chaque item au nombre total d'underscores.
		unders = unders + user.count("_")
	end
	#Rendu plus sympa
	puts "L'array contient " + @twitter.length.to_s + " twitter.\nIl y a " + num.to_s + " twitter contenant au moins un chiffre."
	puts "Il y a " + aude.to_s + " twitter contenant \"aude\"\nIl y a "+ maj.to_s + " twitter commençant par une majuscule"
	puts "Il y a " + maj2.to_s + " twitter contenant au moins une majuscule\nIl y a " +  unders.to_s + " underscores en tout.\n\n"
	print "Voilà la liste triée des twitter de journalistes: " 
	puts @twitter.sort
end
